mapper.select:
	    SELECT 
	    	COUNT(*)
	    FROM
	    	account

	    	account_name = #{accountName}
    	AND account_id != #{accountId}

SELECT 
	COUNT(*)
FROM
	account

	phone_number = #{phoneNumber}
AND account_id != #{accountId}

mapper.update:
UPDATE
	account
SET
		account_name 	= #{accountName}
	, 	password 		= #{password}
	, 	full_name 		= #{fullName}
	, 	phone_number 	= #{phoneNumber}
	, 	version 		= #{version} + 1

		account_id 		= #{accountId}
	AND	version 		= #{version}
