mapper.select:
SELECT 
	COUNT(*)
FROM
	customer

	phone_number = #{phoneNumber}
AND customer_id != #{customerId}

mapper.update:
UPDATE
	customer
SET
		customer_name 		= #{accountName}
	, 	phone_number 		= #{phoneNumber}
	, 	address 			= #{address}
	, 	administrator_id 	= #{accountId}
	, 	version 			= #{version} + 1

		customer_id 		= #{customerId}
	AND	version 		= #{version}
