mapper.update:
UPDATE
	orders
SET
	product_id			= #{productId}
,	unit_price			= #{unitPrice}
,	quantity			= #{quantity}
,	customer_id			= #{customerId}
,	customer_address	= #{address}
,	customer_phone		= #{phone}
,	version				= version + 1

	id 					= #{id}
AND version  			= #{version}
AND order_status_id 	= 1

mapper.select:
SELECT COUNT(*) FROM customers c LEFT JOIN ( SELECT customer_id FROM orders
1 = 1
AND order_date > #{beginDate}
AND order_date < DATE_ADD(#{endDate}, INTERVAL 1 DAY)
)as temp ON c.id = temp.customer_id
c.is_deleted = false AND temp.customer_id IS NULL

SELECT c.id , c.name , c.phone , c.address FROM customers c LEFT JOIN ( SELECT customer_id FROM orders
1 = 1
AND order_date > #{beginDate}
AND order_date < DATE_ADD(#{endDate}, INTERVAL 1 DAY)
)as temp ON c.id = temp.customer_id
c.is_deleted = false AND temp.customer_id IS NULL
ORDER BY SUBSTRING_INDEX(c.name, ' ', -1) LIMIT 3 OFFSET #{offset}

SELECT p.id , p.code , p.name , SUM(o.quantity) as quantity FROM products p JOIN orders o ON o.product_id = p.id
1 = 1
AND order_date > #{beginDate}
AND order_date < DATE_ADD(#{endDate}, INTERVAL 1 DAY)
AND p.is_deleted = FALSE
GROUP BY o.product_id ORDER BY quantity DESC , p.id LIMIT 3 OFFSET #{offset}

SELECT COUNT(*) FROM products p LEFT JOIN ( SELECT product_id FROM orders
1 = 1
AND order_date > #{beginDate}
AND order_date < DATE_ADD(#{endDate}, INTERVAL 1 DAY)
) AS temp ON temp.product_id = p.id
p.is_deleted = FALSE AND temp.product_id IS NULL

SELECT p.id , p.code , p.name FROM products p LEFT JOIN ( SELECT product_id FROM orders
1 = 1
AND order_date > #{beginDate}
AND order_date < DATE_ADD(#{endDate}, INTERVAL 1 DAY)
) AS temp ON temp.product_id = p.id
p.is_deleted = FALSE AND temp.product_id IS NULL
ORDER BY p.code LIMIT 3 OFFSET #{offset}
