mapper.select:
SELECT 
	COUNT(*)
FROM
	products

		code 	= #{code}
	AND id 		!= #{id}

SELECT 
	COUNT(*)
FROM
	products

		name 	= #{name}
	AND id 		!= #{id}

mapper.update:
UPDATE
	products
SET
		code 			= #{code}
	, 	name 			= #{name}
	, 	purchase_price 	= #{purchasePrice}
	, 	sale_price 		= #{salePrice}
	, 	version 		= #{version} + 1

		id 				= #{id}
	AND	version 		= #{version}
